import sdk from 'spoon'
import { observer, useLocalObservable } from 'mobx-react-lite'
import { Box, Button } from '@chakra-ui/react'
//refer to: https://solidity-by-example.org/hello-world/
export default function HelloWorld() {
  const store = useLocalObservable(() => ({
    string: "",
    async great() {
      let res = await sdk.smartgraph.query(`query {
          HelloWorld (calls: {chainId: 4689, address: "0xb3A3A0e0d966970456e3178f9ec03fe4DCbc2deC"}) {
              greet
          }
      }`)
      this.string = res.data.HelloWorld[0].greet
    }
  }))

  const App = observer(() => {
    return <Box bg="white" w="500px" p="2rem" rounded="xl" boxShadow='0px 3px 20px #d6d6d680'>
      <Box>String: {store.string || '-'}</Box>
      <Button colorScheme='purple' mt={4} size="sm" my="4px" onClick={store.great}>start great</Button>
    </Box>
  })

  return <App />
}

